from fpdf import *
from num2words import num2words

data = {
    'buyer': {
        'name': 'Szymon',
        'surname': 'Kasprzycki',
        'nip': None,
        'first_line_addr': 'ul. ĄŻĆxda 145/12',
        'second_line_addr': '00-001 Warsaw'
    },
    'seller': {
        'name': 'Adam',
        'surname': 'Kowalski',
        'nip': '744-982-12-68',
        'first_line_addr': 'ul. Kłosowa 5/1',
        'second_line_addr': '05-111 Warsaw'
    },
    'products': (
        ("Product name", "34", "145", str(34 * 145), "23%", str(34 * 145 * 0.23), str(34 * 145 * 1.23)),
        ("Product name", "34", "145", str(34 * 145), "5%", str(34 * 145 * 0.05), str(34 * 145 * 1.05)),
        ("Product name", "34", "145", str(34 * 145), "23%", str(34 * 145 * 0.23), str(34 * 145 * 1.23)),
        ("Product name", "34", "145", str(34 * 145), "23%", str(34 * 145 * 0.23), str(34 * 145 * 1.23)),
    ),
    'info': {
        'currency': 'USD',
        'invoice_number': '1/07/2022',
        'total_net': 1000.00,
        'total_b': 1230.00,
        'invoice_date': '01.01.2022',
        'invoice_place': 'Warsaw',
        'date_of_service': '01.01.2022',
        'selling_person': 'Jan Nowak',
        'payment_type': 'bank transfer in two days',
        'payment_deadline': '03.01.2022',
        'bank_account_number': '11 0000 1234 9876 1233 9876'
    }
}


class InvoicePDF(FPDF):
    def __init__(self, inv_data: dict, orientation='P', unit='mm', format='A4'):
        super().__init__(orientation=orientation, unit=unit, format=format)
        self.invoice_data = inv_data
        self.add_font('DejaVu', "", 'DejaVuSans.ttf')
        self.add_font("DejaVu", 'B', "DejaVuSans-Bold.ttf")
        self.generate_structure()

    def header(self):
        self.image(x=0, y=0, name='Drawing.png', w=200, h=290)

    def generate_structure(self):
        self.add_page()
        self.add_default_data()
        # Invoice number
        self.ln(27)
        self.add_invoice_num(self.invoice_data['info']['invoice_number'])
        # Buyer and seller
        self.ln(15)
        self.add_seller_buyer(self.invoice_data['seller'], self.invoice_data['buyer'])
        self.add_products()
        self.add_totals()
        self.add_summary()
        self.add_sign_places()

    def add_default_data(self):
        self.image(
            name='data:image/jpeg;base64,',
            x=5, y=5, title='Logo', alt_text='Logo orange', w=50, h=27)

        self.set_font('DejaVu', size=10, style='')
        self.text(y=11, x=135, txt=f'Invoice date: {self.invoice_data["info"]["invoice_date"]}')
        self.text(y=18, x=135, txt=f'Invoice place: {self.invoice_data["info"]["invoice_place"]}')
        self.text(y=25, x=135, txt=f'Date of service: {self.invoice_data["info"]["date_of_service"]}')

    def add_invoice_num(self, iv_number: str):
        self.set_font('DejaVu', size=15, style='B')
        self.cell(w=self.epw, h=15, align='C', txt=f"INVOICE No. {iv_number}")

    def add_seller_buyer(self, seller_data: dict, buyer_data: dict):
        self.set_font('DejaVu', size=9, style="B")
        self.set_fill_color(242)
        s_b_width = (self.epw - self.l_margin) / 2
        self.cell(w=s_b_width, h=9, align='C', txt="Seller", border=1, fill=True)
        self.cell(w=self.l_margin, h=9, txt="")
        self.cell(w=s_b_width, h=9, align='C', txt="Buyer", border=1, fill=True)
        self.ln(11)
        self.set_font('DejaVu', size=9, style="")
        # Seller name
        self.cell(w=s_b_width, h=5, align='L', txt=f'{seller_data["name"]} {seller_data["surname"]}')
        self.cell(w=self.l_margin, h=9, txt="")
        # Buyer name
        self.cell(w=s_b_width, h=5, align='L', txt=f'{buyer_data["name"]} {buyer_data["surname"]}')
        self.ln(5)
        # Seller NIP
        self.cell(w=s_b_width, h=5, align='L', txt=f'NIP: {seller_data["nip"]}')
        self.cell(w=self.l_margin, h=5, txt="")
        # BUYER NIP
        if buyer_data['nip']:
            self.cell(w=s_b_width, h=5, align='L', txt=f'NIP: {buyer_data["nip"]}')
        else:
            self.cell(w=s_b_width, h=5, align='L', txt=f'NIP: none')
        self.ln(5)
        # Seller first address line
        self.cell(w=s_b_width, h=5, align='L', txt=f'{seller_data["first_line_addr"]}')
        self.cell(w=self.l_margin, h=5, txt="")
        # Buyer first address line
        self.cell(w=s_b_width, h=5, align='L', txt=f'{buyer_data["first_line_addr"]}')
        self.ln(5)
        # Seller seecond address line
        self.cell(w=s_b_width, h=5, align='L', txt=f'{seller_data["second_line_addr"]}')
        self.cell(w=self.l_margin, h=5, txt="")
        # Buyer second address line
        self.cell(w=s_b_width, h=5, align='L', txt=f'{buyer_data["second_line_addr"]}')
        self.ln(15)

    def add_products(self):
        line_height = self.font_size * 3
        table_scheme = ('No.', 'Product name', 'Amount', 'Price net', "Total net", 'Tax', 'Tax amount', 'Total brutto')
        self.set_fill_color(240)
        for one_cell in table_scheme:
            self.set_font("DejaVu", size=10, style='B')
            if table_scheme.index(one_cell) == 0:
                col_width = 10
            elif table_scheme.index(one_cell) == 2:
                col_width = 18
            elif table_scheme.index(one_cell) == 5:
                col_width = 18
            elif table_scheme.index(one_cell) in [3, 4, 6, 7]:
                col_width = 21
            else:
                col_width = self.epw - 10 - 18 - 18 - 21 * 4
            self.multi_cell(col_width, line_height, one_cell, border=1,
                            new_x="RIGHT", new_y="TOP", max_line_height=self.font_size * 1.5, align='C', fill=True)
        self.ln(line_height)

        position_number = 1
        self.set_font("DejaVu", size=10)
        for row in data['products']:
            col_width = 10
            self.multi_cell(col_width, line_height, str(position_number), border=1,
                            new_x="RIGHT", new_y="TOP", max_line_height=self.font_size, align='C')
            position_number += 1
            for datum in row:
                # Tutaj match case python 3.10
                if row.index(datum) == 1:
                    col_width = 18
                elif row.index(datum) == 4:
                    col_width = 18
                elif row.index(datum) in [2, 3, 5, 6]:
                    col_width = 21
                else:
                    col_width = self.epw - 10 - 18 - 18 - 21 * 4
                self.multi_cell(col_width, line_height, datum, border=1,
                                new_x="RIGHT", new_y="TOP", max_line_height=self.font_size, align='C')
            self.ln(line_height)

    def add_totals(self):
        line_height = self.font_size * 3
        table_scheme = ('', '', '', 'Total', str(self.invoice_data["info"]["total_net"]), '',
                        str(self.invoice_data["info"]["total_b"] - self.invoice_data["info"]["total_net"]),
                        str(self.invoice_data["info"]["total_b"]))
        for i, one_cell in enumerate(table_scheme):
            self.set_font("DejaVu", size=10, style='B')
            if i == 0:
                col_width = 10
                border = 0
            elif i == 2:
                col_width = 18
                border = 0
            elif i == 5:
                col_width = 18
                border = 1
            elif i in [3, 4, 6, 7]:
                col_width = 21
                border = 1
            else:
                col_width = self.epw - 10 - 18 - 18 - 21 * 4
                border = 0
            self.multi_cell(col_width, line_height, one_cell, border=border,
                            new_x="RIGHT", new_y="TOP", max_line_height=self.font_size * 1.5, align='C')
        self.ln(line_height)

    def add_summary(self):
        width = (self.epw - self.l_margin * 3) / 2
        self.set_font('DejaVu', style='B', size=13)
        self.cell(w=width, h=10, txt='', border='B')
        self.cell(w=self.l_margin * 3, h=10, txt="")
        self.cell(w=width, h=10, txt='', border='B')
        text_width = width / 3
        self.ln()
        self.set_font('DejaVu', style='B', size=9)
        self.cell(w=text_width, h=11, txt='Payment type')
        self.set_font('DejaVu', style='', size=9)
        self.cell(w=text_width * 2, h=11, txt=self.invoice_data['info']['payment_type'], align='C')
        self.cell(w=self.l_margin * 3, h=11, txt='')
        self.set_font('DejaVu', style='B', size=12)
        self.cell(w=width, h=12,
                  txt=f'To pay: {self.invoice_data["info"]["total_b"]} {self.invoice_data["info"]["currency"]}',
                  align='C')
        self.ln()
        self.set_font('DejaVu', style='B', size=9)
        self.cell(w=text_width, h=11, txt='Payment deadline', border='T')
        self.set_font('DejaVu', style='', size=9)
        self.cell(w=text_width * 2, h=11, txt=self.invoice_data['info']['payment_deadline'], align='C', border='T')
        self.cell(w=self.l_margin * 3, h=10, txt="")
        self.set_font('DejaVu', style='', size=7)
        self.cell(w=width, h=10, border="T",
                  txt=f'In words: {num2words(self.invoice_data["info"]["total_b"], ordinal=False, to="currency").replace("euro", "dollars")}')
        self.ln()
        self.set_font('DejaVu', style='B', size=7)
        self.cell(w=text_width, h=11, txt='Bank account number:', border='T')
        self.set_font('DejaVu', style='', size=7)
        self.cell(w=text_width * 2, h=11, txt=f'{self.invoice_data["info"]["bank_account_number"]}', align='C',
                  border='T')

    def add_sign_places(self):
        width = (self.epw - self.l_margin * 3) / 2
        self.ln(25)
        self.set_font('DejaVu', style='B', size=13)
        self.cell(w=width, h=9, txt=self.invoice_data["info"]["selling_person"], align='C')
        self.ln(4)
        self.set_font('DejaVu', style='', size=10)
        self.cell(w=width, h=9, txt='.' * 70)
        self.cell(w=self.l_margin * 3, h=9, txt="")
        self.cell(w=width, h=9, txt='.' * 70)
        self.ln(4)
        self.set_font('DejaVu', style='', size=9)
        self.cell(w=width, h=9, txt='Seller', align='C')
        self.cell(w=self.l_margin * 3, h=9, txt="")
        self.cell(w=width, h=9, txt='Receiver', align='C')

    def footer(self):
        self.set_y(-15)
        self.set_font('DejaVu', '', 8)
        self.cell(w=0, h=10, txt='{}/{}'.format(self.page_no(), '{nb}'), border=0, align='C')

    def save(self):
        self.alias_nb_pages()
        return self.output(dest='S')


if __name__ == '__main__':
    pdf = InvoicePDF(inv_data=data)
    pdf.save(pdf.invoice_data['info']['invoice_number'].replace('/', '-') + '.pdf')
